SET NAMES UTF8;
DROP DATABASE IF EXISTS stu_mysql;
CREATE DATABASE stu_mysql CHARSET=UTF8;
USE stu_mysql;
/*学生表结构*/
CREATE TABLE stu(
  sid INT(9) PRIMARY KEY AUTO_INCREMENT,
  sname VARCHAR(16) not null,
  sex BOOLEAN not null,
  birthday date not null,
  class INT not null,
  phone VARCHAR(11) not null,        
  psd VARCHAR(9)  DEFAULT '12345'                  
);




