SET NAMES UTF8;
DROP DATABASE IF EXISTS stu_mysql;
CREATE DATABASE stu_mysql CHARSET=UTF8;
USE stu_mysql;
/*班级课程表*/
CREATE TABLE class_schedule(  
    cid int NOT NULL PRIMARY KEY,
    class_name VARCHAR(16) not null,
    homeroom_teacher VARCHAR(16) not null,
    course_schedule VARCHAR(16) NOT NULL
);
/*学生表结构*/
CREATE TABLE stu(
  sid INT(9) PRIMARY KEY,
  sname VARCHAR(16) not null,
  sex BOOLEAN not null,
  birthday date not null,
  class INT not null,
  phone VARCHAR(11) not null,        
  psd VARCHAR(9)  DEFAULT '12345',
  FOREIGN KEY(class) REFERENCES  class_schedule(cid)                
);
/*成绩表结构*/
create table score(
	uid int(9),
	Chinese int,
	Math int,
	English int,
	classid int not null,
    /*foreign key(uid) references stu(sid),*/
	foreign key(classid) references class_schedule(cid)
);
/*教师表结构*/
CREATE TABLE teacher(
  tid tinyint PRIMARY KEY AUTO_INCREMENT,
  tname VARCHAR(16) not null,
  tsex BOOLEAN not null,
  tphone CHAR(11) not null,        
  class_id int,
  tpsd VARCHAR(9)  not null DEFAULT '123456',
  foreign key(class_id) references class_schedule(cid)
)charset='utf8';
